    private void GetVM()
    {
        servers.Clear();
        PowerShell ps = PowerShell.Create();
        ps.AddScript("(get-vm).vmname");
        Collection<PSObject> resultName = ps.Invoke();
        count = resultName.Count;
        ps.AddScript("(get-vm).state");
        Collection<PSObject> resultState = ps.Invoke();
        ps.AddScript("(get-vm | select -ExpandProperty networkadapters).ipAddresses");
        Collection<PSObject> resultIP = ps.Invoke();

        for (int i = 0; i < count; i++)
        {
            s.name = resultName[i].ToString();
            s.state = resultState[i].ToString();
            if (s.state != "Off")
                s.ip = resultIP[i].ToString();
            servers.Add(s);
        }

    }
